      createTiles = () => {
      return (
      <div id={this.props.title} className="row" style={row}>
        let parent = [];
        let i = 1;
        this.props.items.map((genre) => {
          parent.push(
          <div className="col" style={colLeft}>
            <genre />
          </div>)
        });
      </div>)}

