      <h2 className="action_games_title" id="action_games">
        <button type="button" className="arrow_btn"><span id="action_games_icon" className="fas fa-caret-up"></span></button>
        Action Games
      </h2>


      <Action_Games title={"action_games_row"} a={"Fighting"} b={"Shooter"} c={"Platformer"} d={"Stealth"} e={"Survival"} f={"Beat-em-up"} />

