    $("[id$='_games_icon']").on("click", function() {
      let genre = this.id.slice(0, -5)
      console.log(genre + "_row")
      $("#action_games_row").slideToggle(800);
      if ($("#action_games_icon").hasClass("fa-caret-up")) {
        $("#action_games_icon").removeClass("fa-caret-up").addClass("fa-caret-down")
        $("#action_games").addClass("genre_title_border")
      }
      else {
        $("#action_games_icon").removeClass("fa-caret-down").addClass("fa-caret-up")
        $("#action_games").removeClass("genre_title_border")
      }
    })
  });


export default App;
